function hexToRgb(b){var d=/^#([\da-fA-F]{2})([\da-fA-F]{2})([\da-fA-F]{2})$/;var c=d.exec(b);var a="rgb("+parseInt(c[1],16)+","+parseInt(c[2],16)+","+parseInt(c[3],16)+")";return a}function hexToRgba(c,b){var e=/^#([\da-fA-F]{2})([\da-fA-F]{2})([\da-fA-F]{2})$/;var d=e.exec(c);var a="rgba("+parseInt(d[1],16)+","+parseInt(d[2],16)+","+parseInt(d[3],16)+","+b+")";return a};