$(function(){$("#form_validation").validate({rules:{checkbox:{required:true},gender:{required:true}},highlight:function(a){$(a).parents(".form-line").addClass("error")},unhighlight:function(a){$(a).parents(".form-line").removeClass("error")},errorPlacement:function(a,b){$(b).parents(".form-group").append(a)}});$("#form_advanced_validation").validate({rules:{date:{customdate:true},creditcard:{creditcard:true}},highlight:function(a){$(a).parents(".form-line").addClass("error")},unhighlight:function(a){$(a).parents(".form-line").removeClass("error")},errorPlacement:function(a,b){$(b).parents(".form-group").append(a)}});$.validator.addMethod("customdate",function(b,a){return b.match(/^\d\d\d\d?-\d\d?-\d\d$/)},"Please enter a date in the format YYYY-MM-DD.");$.validator.addMethod("creditcard",function(b,a){return b.match(/^\d\d\d\d?-\d\d\d\d?-\d\d\d\d?-\d\d\d\d$/)},"Please enter a credit card in the format XXXX-XXXX-XXXX-XXXX.")});