$(function(){$(".js-sweetalert button").on("click",function(){var a=$(this).data("type");if(a==="basic"){showBasicMessage()}else{if(a==="with-title"){showWithTitleMessage()}else{if(a==="success"){showSuccessMessage()}else{if(a==="confirm"){showConfirmMessage()}else{if(a==="cancel"){showCancelMessage()}else{if(a==="with-custom-icon"){showWithCustomIconMessage()}else{if(a==="html-message"){showHtmlMessage()}else{if(a==="autoclose-timer"){showAutoCloseTimerMessage()}else{if(a==="prompt"){showPromptMessage()}else{if(a==="ajax-loader"){showAjaxLoaderMessage()}}}}}}}}}}})});function showBasicMessage(){swal("این یک پیام است!")}function showWithTitleMessage(){swal("این یک پیام است!","آیا زیبا نیست؟")}function showSuccessMessage(){swal("عالی بود!","شما بر روی دکمه کلیک کرده اید!","success")}function showConfirmMessage(){swal({title:"آیا شما مطئعن هستید؟",text:"شما نمی توانید دیگر این فایل را بازگردانید.",type:"warning",showCancelButton:true,confirmButtonColor:"#DD6B55",confirmButtonText:"بله آن را پاک کن!",cancelButtonText:"نه دست نگه دار",closeOnConfirm:false},function(){swal("پاک شد!","فایل مورد نظر پاک شد.","success")})}function showCancelMessage(){swal({title:"آیا شما مطئعن هستید؟",text:"شما نمی توانید دیگر این فایل را بازگردانید.",type:"warning",showCancelButton:true,confirmButtonColor:"#DD6B55",confirmButtonText:"بله آن را پاک کن",cancelButtonText:"نه دست نگه دار",closeOnConfirm:false,closeOnCancel:false},function(a){if(a){swal("پاک شد!","فایل مورد نظر پاک شد.","success")}else{swal("لغو شد.","فایل مورد نظر پاک نشد.","error")}})}function showWithCustomIconMessage(){swal({title:"عالیه!",text:"این یک عکس سفارشی هست.",imageUrl:"../../images/thumbs-up.png"})}function showHtmlMessage(){swal({title:"HTML <small>عنوان</small>!",text:'یک متن سفارسی <span style="color: #CC0000"> تست اول<span>',html:true})}function showAutoCloseTimerMessage(){swal({title:"به صورت خودکار بسته خواهد شد.",text:"من بعد از دو ثانیه بسته خواهم شد.",timer:2000,showConfirmButton:false})}function showPromptMessage(){swal({title:"یک ورودی",text:"یک چیز جالب بنویسید.",type:"input",showCancelButton:true,closeOnConfirm:false,animation:"slide-from-top",inputPlaceholder:"یک چیزی بنویسید."},function(a){if(a===false){return false}if(a===""){swal.showInputError("شما نیاز دارید چیزی بنویسید.");return false}swal("عالیخ!","شما نویشته اید: "+a,"success")})}function showAjaxLoaderMessage(){swal({title:"نمونه در خواست ajax",text:"برای ثبت  کلیک کنید.",type:"info",showCancelButton:true,closeOnConfirm:false,showLoaderOnConfirm:true},function(){setTimeout(function(){swal("درخواست ajax پایان یافت")},2000)})};