$(function(){initCounters();initCharts()});function initCounters(){$(".count-to").countTo({formatter:function(b,a){return Persian_Number(b.toFixed(2).replace(/(\d)(?=(\d\d\d)+(?!\d))/g," ").replace(".",","))}})}function initCharts(){$.each($(".chart.chart-bar"),function(c,b){var a=$.AdminBSB.options.colors[$(b).data("chartcolor")];$(b).sparkline(undefined,{type:"bar",barColor:a,negBarColor:a,barWidth:"8px",height:"34px"})});$.each($(".chart.chart-pie"),function(c,b){var a=$.AdminBSB.options.colors[$(b).data("chartcolor")];$(b).sparkline(undefined,{type:"pie",height:"50px",sliceColors:[hexToRgba(a,"0.55"),hexToRgba(a,"0.70"),hexToRgba(a,"0.85"),hexToRgba(a,"1")]})});$.each($(".chart.chart-line"),function(c,b){var a=$.AdminBSB.options.colors[$(b).data("chartcolor")];$(b).sparkline(undefined,{type:"line",width:"60px",height:"45px",lineColor:a,lineWidth:1.3,fillColor:"rgba(0,0,0,0)",spotColor:a,maxSpotColor:a,minSpotColor:a,spotRadius:3,highlightSpotColor:a})})};